Stitcher.RawDefinition:
{
  "Type": "Task",
  "Comment": "Combine chunk outputs and emit final artifacts",
  "Resource": "arn:aws:states:::lambda:invoke",
  "OutputPath": "$.Payload",
  "Parameters": {
    "FunctionName": "arn:aws:lambda:eu-west-1:155186308102:function:openai-whisper-stitcher",
    "Payload": {
      "manifest_bucket": "nimbus-transcribe-ingest-155186308102-eu-west-1",
      "results_bucket": "nimbus-transcribe-results-155186308102-eu-west-1",
      "manifest_key.$": "$.manifest_key",
      "language.$": "$.language"
    }
  },
  "Retry": [
    {
      "ErrorEquals": [
        "Lambda.ServiceException",
        "Lambda.AWSLambdaException",
        "Lambda.SdkClientException"
      ],
      "IntervalSeconds": 2,
      "BackoffRate": 2,
      "MaxAttempts": 4
    }
  ],
  "End": true
}

stateMachine.definition:
{
  "Comment": "Openai Whisper Distributed Map over manifest.jsonl -> Batch GPU jobs",
  "StartAt": "FanOutNimbusTranscribes",
  "States": {
    "FanOutNimbusTranscribes": {
      "Type": "Map",
      "MaxConcurrency": 100,
      "ItemReader": {
        "Resource": "arn:aws:states:::s3:getObject",
        "ReaderConfig": { "InputType": "JSONL" },
        "Parameters": {
          "Bucket.$": "$.manifest_bucket",
          "Key.$": "$.manifest_key"
        }
      },
      "ItemSelector": {
        "chunk.$": "$$.Map.Item.Value",
        "results_bucket.$": "$.results_bucket",
        "model.$": "$.model",
        "language.$": "$.language",
        "compute_type.$": "$.compute_type",
        "beam_size.$": "$.beam_size",
        "vad.$": "$.vad"
      },
      "ItemProcessor": {
        "ProcessorConfig": { "Mode": "DISTRIBUTED", "ExecutionType": "STANDARD" },
        "StartAt": "SubmitBatch",
        "States": {
          "SubmitBatch": {
            "Type": "Task",
            "Resource": "arn:aws:states:::batch:submitJob.sync",
            "Parameters": {
              "JobName.$": "States.Format('whisper-{}', $.chunk.job_id)",
              "JobQueue": "arn:aws:batch:eu-west-1:155186308102:job-queue/openai-whisper-gpu-queue",
              "JobDefinition": "arn:aws:batch:eu-west-1:155186308102:job-definition/openai-whisper-transcribe-job:1",
              "ContainerOverrides": {
                "Vcpus": 4,
                "Memory": 11000,
                "Environment": [
                  { "Name": "IN_URI",  "Value.$": "$.chunk.in_uri" },
                  { "Name": "OUT_URI",     "Value.$": "States.Format('s3://{}/chunks/{}/{}/out.json', $.results_bucket, $.chunk.job_id, $.chunk.index)" },
                  { "Name": "CHUNK_INDEX", "Value.$": "States.Format('{}', $.chunk.index)" },
                  { "Name": "OUT_BUCKET",    "Value.$": "$.results_bucket" },
                  { "Name": "OUT_PREFIX",     "Value.$": "States.Format('chunks/{}/{}/', $.chunk.job_id, $.chunk.index)" },
                  { "Name": "RESULTS_PREFIX", "Value.$": "States.Format('chunks/{}/{}/', $.chunk.job_id, $.chunk.index)" },
                  { "Name": "MODEL",     "Value.$": "$.model" },
                  { "Name": "LANGUAGE",  "Value.$": "$.language" },
                  { "Name": "COMPUTE_TYPE","Value.$": "$.compute_type" },
                  { "Name": "BEAM_SIZE", "Value.$": "States.Format('{}', $.beam_size)" },
                  { "Name": "VAD",       "Value.$": "States.Format('{}', $.vad)" },
                  { "Name": "HF_HUB_ENABLE_HF_TRANSFER", "Value": "0" },
                  { "Name": "HF_ENDPOINT",               "Value": "https://huggingface.co" }
                ]
              }
            },
            "ResultPath": "$.batch",
            "End": true
          }
        }
      },
      "ResultPath": "$.chunks",
      "Label": "Chunks",
      "Next": "Stitcher"
    },

    "Stitcher": {
      "Type": "Task",
      "Comment": "Combine chunk outputs and emit final artifacts",
      "Resource": "arn:aws:states:::lambda:invoke",
      "OutputPath": "$.Payload",
      "Parameters": {
        "FunctionName": "arn:aws:lambda:eu-west-1:155186308102:function:openai-whisper-stitcher",
        "Payload": {
          "manifest_bucket": "nimbus-transcribe-ingest-155186308102-eu-west-1",
          "results_bucket": "nimbus-transcribe-results-155186308102-eu-west-1",
          "manifest_key.$": "$.manifest_key",
          "language.$": "$.language"
        }
      },
      "Retry": [
        {
          "ErrorEquals": [
            "Lambda.ServiceException",
            "Lambda.AWSLambdaException",
            "Lambda.SdkClientException"
          ],
          "IntervalSeconds": 2,
          "BackoffRate": 2.0,
          "MaxAttempts": 4
        }
      ],
      "End": true
    }
  }
}
